SELECT a.id as id, a.`title` as title, a.summary as summary, a.`edit_time` as edit_time, a.`page_view` as page_view, a.`state` as state , a.`create_time` as create_time , a.uid as uid, u.`nickname` as nickname, c.`cate_name` as cate_name FROM article a, `user` u, category c WHERE a.`cid`=c.`id` AND a.`uid`=u.`id`
and a.state = #{state}
AND title LIKE concat('%',#{keywords},'%')
ORDER BY a.id DESC limit #{offset},#{count};

